const tokens = marked.lexer(markdown);

render() {
  return (
    <div>
      {tokens.map(token => this.renderToken(token))}
    </div>
  );
}

renderToken(token) {
  return <wsx-marked-heading level={token.depth} text={token.text} />;
}

